import React, { useState } from 'react';
import {useHistory} from "react-router-dom";
import CreatePostForm from '../../components/CreatePostForm';

function CreatePun() {
    const [pun, setPun] = useState({});
    const history = useHistory(); // Initate
    console.log(history);

    const handleChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setPun({
            ...pun,
            [name]: value
        })
    }

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            await fetch('https://puns-app.herokuapp.com/puns', {
                method: 'POST', // GET, POST, PATCH, DELETE
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(pun) // body data type must match "Content-Type" header
            });
    
            // window.location.replace('/manage-puns') // redirects to the index.html page
            // A smoother redirecting, without a page reload
            history.push('/manage-puns')
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <div>
            <h1>Create Pun</h1>

            <CreatePostForm
                handleSubmit={handleSubmit}
                handleChange={handleChange}
                pun={pun}
                pageId="create-page"
            />

            {/* <form onSubmit={handleSubmit}>
                <textarea 
                    name="content" 
                    value={pun.content}
                    onChange={handleChange}
                    cols="30" 
                    rows="10"
                ></textarea>
                
                <br />
                <br />
                
                <button>Create</button>
                <br />
                <br />
                <Link to="/manage-puns">&larr; Back</Link>
            </form> */}
        </div>
    )
}

export default CreatePun
